import { useState } from "react";
import BookCreate from "./components/BookCreate";

function App() {
	// states
	const [books, setBooks] = useState([]);

	// handlers
	const createBook = (book) => {
		console.log(books);
		setBooks(books.concat[book]);
	};

	return (
		<div>
			<BookCreate onCreate={createBook} />
		</div>
	);
}

export default App;
